defmodule GameWeb.ImageController do
  use GameWeb, :controller

  def cat(conn, _params) do
    conn
    |> put_resp_content_type("image/png")
    |> send_resp(200, Base.decode64!(cat_data()))
  end

  def obstacle(conn, _params) do
    conn
    |> put_resp_content_type("image/png")
    |> send_resp(200, Base.decode64!(obstacle_data()))
  end

  # Simple cat image (pink square with features)
  defp cat_data do
    ""
  end

  # Simple obstacle image (gray block)
  defp obstacle_data do
    ""
  end
end
